mapper.select:
SELECT tasks.*, devices.number deviceNumber, groups.name groupName, configurations.name configurationName, CASE WHEN tasks.scope='device' THEN devices.number WHEN tasks.scope='group' THEN groups.name WHEN tasks.scope='configuration' THEN configurations.name ELSE 'all' END AS target FROM plugin_push_schedule tasks LEFT JOIN devices ON devices.id=tasks.deviceId LEFT JOIN groups ON groups.id=tasks.groupId LEFT JOIN configurations ON configurations.id=tasks.configurationId WHERE tasks.customerId = #{customerId}
AND ( tasks.messageType ILIKE #{messageFilter} OR tasks.payload ILIKE #{messageFilter} OR tasks.comment ILIKE #{messageFilter} OR devices.number ILIKE #{messageFilter} OR groups.name ILIKE #{messageFilter} OR configurations.name ILIKE #{messageFilter} )
ORDER BY id OFFSET (#{pageNum} - 1) * #{pageSize} LIMIT #{pageSize}
